script:
frappe.ui.form.on('Sales Invoice', {
	refresh(frm) {
		// your code here
	}
})

frappe.ui.form.on("Sales Invoice Item", "item_code", function(frm, cdt, cdn) {
    var d = locals[cdt][cdn];
        frappe.call({
    method: 'last_price.api.get_selling_price',
    args: {
        'customer': frm.doc.customer,
        'item': d.item_code,

    },
    callback: function(r) {
        if (!r.exc) {

            if (r.message && r.message.length > 0) {
                d.custom_customer_price = r.message[-1].price;
                if (r.message[-1].price === 0) {
                    console.log(`No Customer Price`);
                }
            } else {
                console.log(`No price information available for the customer and item.`);
            }
        } else {
            console.log('Error fetching customer price:', r.exc);
        }
    }
});
});
